import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Login from './Pages/Login';
import HeadmasterDashboard from './Pages/HeadmasterDashboard';
import FacultyDashboard from './Pages/FacultyDashboard';
import ParentDashboard from './Pages/ParentDashboard';
import ProtectedRoute from './components/ProtectedRoute';  // Import ProtectedRoute

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [role, setRole] = useState(null); // Store user role after login

  useEffect(() => {
    // Check localStorage for authentication data (e.g., JWT token)
    const authToken = localStorage.getItem('authToken');
    const userRole = localStorage.getItem('userRole');
    if (authToken && userRole) {
      setIsAuthenticated(true);
      setRole(userRole);
    }
  }, []);

  const handleLogin = (userRole) => {
    setIsAuthenticated(true);
    setRole(userRole);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setRole(null);
    localStorage.clear();  // Clear token and role from localStorage
  };

  return (
    <Router>
      <Header isAuthenticated={isAuthenticated} handleLogout={handleLogout} />

      <Routes>
        <Route path="/login" element={<Login handleLogin={handleLogin} />} />

        {/* Protected Route for Dashboard */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              {role === 'headmaster' ? <HeadmasterDashboard /> :
               role === 'faculty' ? <FacultyDashboard /> :
               role === 'parent' ? <ParentDashboard /> :
               <Navigate to="/login" />}
            </ProtectedRoute>
          }
        />

        <Route path="*" element={<Navigate to="/login" />} /> {/* Redirect any unknown routes */}
      </Routes>
    </Router>
  );
}

export default App;
