import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header.jsx';
import HeadmasterDashboard from './components/Pages/HeadmasterDashboard';
import TeacherDashboard from './components/Pages/TeacherDashboard';
import StudentDetails from './Pages/StudentDetail';
import Login from './Pages/Login';
import { useState, useEffect } from 'react';

const ProtectedRoute = ({ isAuthenticated, children }) => {
  return isAuthenticated ? children : <Navigate to="/" />;
};

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check token in localStorage when the app mounts
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) setIsAuthenticated(true); // Assume user is authenticated if token exists
  }, []);

  return (
    <Router>
      {isAuthenticated && <Header />} {/* Show Header only when authenticated */}

      <div className="container mx-auto p-4">
        <Routes>
          {/* Public Route */}
          <Route path="/" element={<Login />} />

          {/* Protected Routes */}
          <Route
            path="/headmaster-dashboard"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <HeadmasterDashboard />
              </ProtectedRoute>
            }
          />
          <Route
            path="/teacher"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <TeacherDashboard />
              </ProtectedRoute>
            }
          />
          <Route
            path="/student/:id"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <StudentDetails />
              </ProtectedRoute>
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
