// File: src/Pages/Login.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Styles/Login.css'; // Assuming CSS is already set up
import api from '../Utils/api'; // Axios instance

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // To show login errors
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/auth/login', { email, password }); // Axios POST request
      const data = response.data;

      if (data.user) {
        // Save JWT token in localStorage
        localStorage.setItem('token', data.token);

        // Role-based redirection
        switch (data.user.role) {
          case 'headmaster':
            navigate('/headmaster-dashboard');
            break;
          case 'faculty':
            navigate('/faculty-dashboard');
            break;
          case 'parent':
            navigate('/parent-dashboard');
            break;
          default:
            alert('Invalid role');
        }
      } else {
        setError(data.error || 'Login failed'); // Handle login error from backend
      }
    } catch (err) {
      console.error(err);
      setError('Something went wrong. Please try again later.');
    }
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <form className="login-form" onSubmit={handleLogin}>
        {error && <p className="error-message">{error}</p>} {/* Show error if exists */}
        
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
