import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState } from 'react'; // For authentication state
import Header from './components/Header.jsx';
import HeadmasterDashboard from './components/Dashboard/HeadmasterDashboard';
import TeacherDashboard from './components/Dashboard/TeacherDashboard';
import StudentDetails from './Pages/StudentDetail';
import Login from './Pages/Login';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Example auth state

  return (
    <Router>
      <Header />
      <div className="container mx-auto p-4">
        <Routes>
          <Route path="/" element={<Login />} />
          <Route
            path="/headmaster"
            element={isAuthenticated ? <HeadmasterDashboard /> : <Navigate to="/" />}
          />
          <Route
            path="/teacher"
            element={isAuthenticated ? <TeacherDashboard /> : <Navigate to="/" />}
          />
          <Route
            path="/student/:id"
            element={isAuthenticated ? <StudentDetails /> : <Navigate to="/" />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
