import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Styles/HeadmasterDashboard.css';

const HeadmasterDashboard = () => {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/api/headmaster/students')
      .then((res) => res.json())
      .then((data) => setStudents(data))
      .catch((err) => console.error(err));
  }, []);

  const handleAddStudent = () => {
    navigate('/headmaster/students/add');
  };

  return (
    <div className="headmaster-dashboard">
      <h1>Headmaster Dashboard</h1>
      <button className="add-student-button" onClick={handleAddStudent}>
        Add Student
      </button>
      <div className="students-list">
        {students.map((student) => (
          <div key={student.studentId} className="student-card">
            <h2>{student.name}</h2>
            <p>Age: {student.age}</p>
            <p>Grade: {student.grade}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HeadmasterDashboard;
