// HeadmasterDashboard.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Styles/HeadmasterDashboard.css';

const HeadmasterDashboard = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await fetch('/api/headmaster/students');
        if (!response.ok) throw new Error('Failed to fetch student data.');
        const data = await response.json();
        setStudents(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, []);

  const handleAddStudent = () => navigate('/headmaster/students/add');

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Welcome, Headmaster</h1>
        <button className="add-student-button" onClick={handleAddStudent}>
          + Add New Student
        </button>
      </header>

      <section className="school-overview">
        <h2>School Overview</h2>
        {loading && <p className="loading-message">Loading student data...</p>}
        {error && <p className="error-message">{error}</p>}
        {!loading && !error && students.length === 0 && (
          <p>No students found. Please add new students.</p>
        )}

        <div className="students-grid">
          {students.map((student) => (
            <div key={student.studentId} className="student-card">
              <h3>{student.name}</h3>
              <p>Age: {student.age}</p>
              <p>Grade: {student.grade}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HeadmasterDashboard;
