import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Login from './Pages/Login';
import HeadmasterDashboard from './Pages/HeadmasterDashboard';
import FacultyDashboard from './Pages/FacultyDashboard';
import ParentDashboard from './Pages/ParentDashboard';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [role, setRole] = useState(null);

  // Check localStorage to persist login after a page refresh
  useEffect(() => {
    const storedRole = localStorage.getItem('userRole');
    const token = localStorage.getItem('authToken');
    if (storedRole && token) {
      setIsAuthenticated(true); // If token is found, mark the user as authenticated
      setRole(storedRole); // Set the stored role
    }
  }, []);

  const handleLogin = (userRole) => {
    setIsAuthenticated(true);
    setRole(userRole);
    localStorage.setItem('userRole', userRole); // Store role in localStorage for persistence
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setRole(null);
    localStorage.removeItem('userRole'); // Clear localStorage on logout
    localStorage.removeItem('authToken');
  };

  return (
    <Router>
      <Header isAuthenticated={isAuthenticated} handleLogout={handleLogout} />

      <Routes>
        <Route path="/login" element={<Login handleLogin={handleLogin} />} />

        {/* Role-based Dashboard Routes */}
        <Route
          path="/dashboard"
          element={
            isAuthenticated ? (
              role === 'headmaster' ? (
                <HeadmasterDashboard />
              ) : role === 'faculty' ? (
                <FacultyDashboard />
              ) : role === 'parent' ? (
                <ParentDashboard />
              ) : (
                <Navigate to="/login" />
              )
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route path="*" element={<Navigate to="/login" />} /> {/* Redirect any unknown routes */}
      </Routes>
    </Router>
  );
}

export default App;
