import React, { useState } from 'react';

const HeadmasterDashboard = () => {
  const [showFacultyForm, setShowFacultyForm] = useState(false); // State to toggle the pop-up form
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    address: '',
    gender: '',
    phoneNumber: '',
  });

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log('Faculty Data Submitted:', formData);
    alert('Faculty added successfully!');
    setFormData({ name: '', email: '', address: '', gender: '', phoneNumber: '' }); // Reset form
    setShowFacultyForm(false); // Close the form after submission
  };

  return (
    <div className="dashboard" style={{ padding: '20px' }}>
      <h1>Headmaster Dashboard</h1>

      <div className="dashboard-cards" style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '20px' }}>
        <div className="card" style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '5px', width: '150px' }}>
          <h3>Total Students</h3>
          <p>150</p>
        </div>

        <div className="card" style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '5px', width: '150px' }}>
          <h3>Total Faculty</h3>
          <p>20</p>
        </div>

        <div className="card" style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '5px', width: '150px' }}>
          <h3>New Notifications</h3>
          <p>5</p>
        </div>
      </div>

      <div className="dashboard-section" style={{ textAlign: 'center', marginBottom: '20px' }}>
        <h2>Manage Students and Faculty</h2>
        <button
          style={{ padding: '10px 20px', margin: '10px', cursor: 'pointer' }}
          onClick={() => console.log('Navigating to student registration')}
        >
          Add New Student
        </button>
        <button
          style={{ padding: '10px 20px', margin: '10px', cursor: 'pointer' }}
          onClick={() => setShowFacultyForm(true)}
        >
          Add New Faculty
        </button>
      </div>

      {/* Pop-Up Faculty Form */}
      {showFacultyForm && (
        <div
          style={{
            position: 'fixed',
            top: '0',
            left: '0',
            width: '100vw',
            height: '100vh',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <div
            style={{
              backgroundColor: 'white',
              padding: '30px',
              borderRadius: '10px',
              width: '400px',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            }}
          >
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add New Faculty</h2>
            <form onSubmit={handleFormSubmit}>
              <input
                type="text"
                placeholder="Name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  marginBottom: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                }}
              />
              <input
                type="email"
                placeholder="Email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  marginBottom: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                }}
              />
              <input
                type="text"
                placeholder="Address"
                value={formData.address}
                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  marginBottom: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                }}
              />
              <select
                value={formData.gender}
                onChange={(e) => setFormData({ ...formData, gender: e.target.value })}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  marginBottom: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                }}
              >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
              <input
                type="text"
                placeholder="Phone Number"
                value={formData.phoneNumber}
                onChange={(e) => setFormData({ ...formData, phoneNumber: e.target.value })}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  marginBottom: '20px',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                }}
              />
              <div style={{ textAlign: 'center' }}>
                <button
                  type="submit"
                  style={{
                    padding: '10px 20px',
                    backgroundColor: '#4CAF50',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    marginRight: '10px',
                  }}
                >
                  Submit
                </button>
                <button
                  type="button"
                  onClick={() => setShowFacultyForm(false)}
                  style={{
                    padding: '10px 20px',
                    backgroundColor: '#f44336',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default HeadmasterDashboard;
